jQuery(document).ready(function($) {
	$window = $('.innerWrapper');
	viewsSite = 0;
	function callLoader(){
		$('#loader').toggleClass('active');
	};
	function callFlexslider(){
		$('.flexslider').flexslider({
	    	animation: "fade",
			slideshow: false,
			directionNav: false,
			controlNav: false,
			slideshowSpeed: 5000,
			animationSpeed: 1000,
			initDelay: 0,
			start: function(slider) { // Fires when the slider loads the first slide
				var slide_count = slider.count - 1;
				$(slider)
					.find('img.lazy:eq(0)')
					.each(function() {
						var src = $(this).attr('data-src');
						$(this).attr('src', src).removeAttr('data-src');
					});
				$(slider).flexslider("play");
			},
			before: function(slider) { // Fires asynchronously with each slider animation
				var slides     = slider.slides,
					index      = slider.animatingTo,
					$slide     = $(slides[index]),
					$img       = $slide.find('img[data-src]'),
					current    = index,
					nxt_slide  = current + 1,
					prev_slide = current - 1;
				$slide
					.parent()
					.find('img.lazy:eq(' + current + '), img.lazy:eq(' + prev_slide + '), img.lazy:eq(' + nxt_slide + ')')
					.each(function() {
						var src = $(this).attr('data-src');
						$(this).attr('src', src).removeAttr('data-src');
					});
			}
		});
	};
	function btBackOrLogo() {
		if ( viewsSite >= 1 ){
			$('#header h1').addClass('hide');
			$('#header .backBt').addClass('active');
		} else {
			$('#header h1').removeClass('hide');
			$('#header .backBt').removeClass('active');
		}
	};
	function doParallax(holder) {
		$('.parallax').each(function(){
			var $bgobj = $(this); // assigning the object
			var speed = $(this).data("speed");
			holder.scroll(function() {
				var yPos = -(holder.scrollTop() / speed);
				var coords = '50% '+ yPos + 'px';
				$bgobj.css({ backgroundPosition: coords });
			});
		});
	};
	function menuOpenClose(){
		$('#menuWrapper').toggleClass('active');
		$('#header h1, #header .menuBt, #header .backBt').toggleClass('menuIsOpen');
		$('#loader').toggleClass('menuIsOpen');
	};
	function preUpdatePage(response){
		NProgress.inc();
		result = $(response).filter(".innerWrapper");
		result.addClass('active beforeShow');
		actualInnerWrapper = $('body').find('.innerWrapper');
		bodyClassesResponse = response.match(/body class=\"(.*?)\"/)[1];
		bodyClassesActual = $('body').attr('class');
		classesTransition = bodyClassesActual + ' ' + bodyClassesResponse;
		titleResponse = $(response).filter('title').text();
		$('body').append(result);
	};
	function updatePage(result, classesTransition, titleResponse, actualInnerWrapper, bodyClassesResponse) {
			setTimeout(function(){ 
				$("body").attr("class", classesTransition);
				document.title = titleResponse;
				actualInnerWrapper.removeClass('active'); 
				result.removeClass('beforeShow'); 
				setTimeout(function(){ 
					$("body").attr("class", bodyClassesResponse);
					$('body').addClass('historyPush');
					btBackOrLogo();
					NProgress.done(); 
					callLoader();
				},500);
			},2500);
		if (result.find('.flexslider').length >= 1) { callFlexslider(); }
		doParallax(result);
		langEspUrl = result.find('#footer .lang a.esp').attr('href').split('?');
		langEngUrl = result.find('#footer .lang a.eng').attr('href').split('?');
		result.find('#footer .lang a.esp').attr('href', langEspUrl[0]);
		result.find('#footer .lang a.eng').attr('href', langEngUrl[0]);
	};
	/*$window.imagesLoaded( function() {
		NProgress.done(); 
		callLoader();
	});*/
	setTimeout(function(){ 
		NProgress.done(); 
		callLoader();
	},2500);
	callFlexslider();
	btBackOrLogo();
	doParallax($window);
	$( document ).on( "click", "#menuBt", function() {
		menuOpenClose();
	});
	$( document ).keyup(function(e) {
		if (e.keyCode == 27 && $( "#menuBt" ).hasClass( "menuIsOpen" )) { menuOpenClose(); }
	});
	$( document ).on( "click", "a.callPageTransition", function() {
		/*
		if uncomment the above line, html5 nonsupported browers won't change the url but will display the ajax content;
		if commented, html5 nonsupported browers will reload the page to the specified link.
		*/
		pageurl = $(this).attr('href');
		if(pageurl==window.location || pageurl.indexOf('works')>1){ return false; };
        callLoader();
        NProgress.start();
		if ( $( "#menuBt" ).hasClass( "menuIsOpen" ) ){ menuOpenClose(); }
		$.ajax({
			url: pageurl,
			cache: false,
			success: function(response){
				preUpdatePage(response);
				/*setTimeout(function(){ 
					viewsSite++;
					updatePage(result, classesTransition, titleResponse, actualInnerWrapper, bodyClassesResponse);
				},500);*/
			},
			complete: function(){
				viewsSite++;
				updatePage(result, classesTransition, titleResponse, actualInnerWrapper, bodyClassesResponse);
			}
		});
		if(pageurl!=window.location){
			window.history.pushState({path:pageurl},'',pageurl);
		};
		return false;
	});
	$(window).bind('popstate', function() {
		if ( $('body').hasClass('historyPush') ) {
	        callLoader();
	        NProgress.start();
			if ( $( "#menuBt" ).hasClass( "menuIsOpen" ) ){ menuOpenClose(); }
			$.ajax({
				url:location.pathname, 
				cache: false, 
				success: function(response){
					preUpdatePage(response);
					/*setTimeout(function(){ 
						viewsSite--;
						updatePage(result, classesTransition, titleResponse, actualInnerWrapper, bodyClassesResponse);
					},500);*/
				},
				complete: function(){
					viewsSite--;
					updatePage(result, classesTransition, titleResponse, actualInnerWrapper, bodyClassesResponse);
				}
			});
		}
	});
	NProgress.start(); 
	NProgress.configure({ parent: '#loader', showSpinner: false });
});
$(window).load(function() {
	var logo = $('#header h1');
	var distance = $('.content').offset().top - logo.outerHeight();
	$window.scroll(function() {
	    if ( $window.scrollTop() >= distance ) {
			logo.addClass('hide');
	    } else {
		    logo.removeClass('hide');
	    }
	});
});
